if (window.innerWidth < 768) {
    document.querySelectorAll("[data-bss-disabled-mobile]").forEach((element) => {
      element.classList.remove("animated");
      element.removeAttribute("data-bss-hover-animate");
      element.removeAttribute("data-aos");
      element.removeAttribute("data-bss-parallax-bg");
      element.removeAttribute("data-bss-scroll-zoom");
    });
  }
  
  document.addEventListener("DOMContentLoaded", () => {
    if ("AOS" in window) {
      AOS.init();
    }
  });
  

  document.addEventListener("contextmenu", (event) => {
    event.preventDefault();
  });


  document.onkeydown = function (event) {
    if (event.keyCode !== 123 && ((!event.metaKey || !event.altKey || event.keyCode !== 73) &&
        (!event.ctrlKey || !event.altKey || event.keyCode !== 74) &&
        (!event.metaKey || event.keyCode !== 85))) {
      return void 0;
    }
  };

